'use strict';

// tests for accountUpdate
// Generated by serverless-mocha-plugin

const mochaPlugin = require('serverless-mocha-plugin');
const expect = mochaPlugin.chai.expect;
let wrapped = mochaPlugin.getWrapper('accountUpdate', '/controllers/account/update.js', 'handler');

describe('accountUpdate', () => {
  before((done) => {
    done();
  });

  it('username : <<<<>>>>>', () => {
    const event = {
      principalId: "google-oauth2|101778494068951192848",
      body: {
        username: "<<<<>>>>>"
      }
    }
    return wrapped.run(event).then((response) => {
      expect(response).to.not.be.empty;
    });
  });

  it('다른사람의 username 중복 : ddddd', () => {
    const event = {
      principalId: "google-oauth2|101778494068951192848",
      body: {
        username: "ddddd"
      }
    }
    return wrapped.run(event).then((response) => {
      expect(response).to.not.be.empty;
    });
  });

});
